mapper.insert:
INSERT INTO user_licenses (
    licenses_seq,
    email,
    reg_dt
) VALUES (
    #{licensesSeq},
    #{email}, 
    #{regDt} 
)

mapper.delete:
DELETE FROM user_licenses
WHERE
    email = #{email}
    AND licenses_seq = #{licensesSeq}
    AND reg_dt = #{regDt}

mapper.update:
UPDATE 
    user_licenses
SET
    reg_dt = #{regDt}
WHERE
    email = #{email}
    AND licenses_seq = #{licensesSeq}
